import App from "./App";
import React from "react";

class Pokemon extends React.Component {
  render() {
    const { id, name, type, averageWeight:{value: val}, averageWeight:{measurementUnit: kg}, image } = this.props.poke;
    return (
      <div key={id}>
        <p>{name}</p>
        <p>{type}</p>
        <p>{`Average Weight: ${val}${kg}`}</p>
        <img src="{image}" alt="{name}" />
      </div>
    );
  }
}

export default Pokemon;
