          <button onClick={this.chamaOProximo}>Próximo Pokémon</button>
          <Button tipo={'Fire'} filtraPoke={this.filtraPoke}/>
          <Button tipo={'Psychic'} filtraPoke={this.filtraPoke}/>
        </div>
    );
  }
};

export default Pokedex;
