import React from "react";
import Pokemon from "./pokemon"
import Select from "./Select";

class Pokedex extends React.Component {
  constructor() {
    super();
    this.state = {
      pokemonIndex: 0, 
    };
    this.chamaOProximo = this.chamaOProximo.bind(this);
    // this.tamanhoPoke = this.tamanhoPoke.bind(this);
  };

  chamaOProximo() {
    const pokemons = this.props.poke;
    const { pokemonIndex } = this.state;
    pokemonIndex === pokemons.length - 1
    ? this.setState({pokemonIndex: 0})
    : this.setState((prevState) => ({pokemonIndex: prevState.pokemonIndex + 1}))
  }

// filtraPoke() {
//   const pokemons = this.props.poke;
//   const pokeFiltrado = pokemons.filter((pokemon, index) => {pokemon[index] === this.state.pokemonIndex})
//   return pokeFiltrado;
// }

  render() {
    const pokemons = this.props.poke;
    const { pokemonIndex } = this.state;
    console.log(pokemonIndex);
    return (
        <div>
          <Pokemon poke={ pokemons[pokemonIndex] }/>
          <button onClick={this.chamaOProximo}>Próximo Pokémon</button> 
        </div>
    );
  }
};

export default Pokedex;
